mapper.update:
UPDATE purchase SET
is_agree=#{isAgree}
WHERE
purchase_id
IN


	#{purchaseId}

mapper.select:
SELECT purchase_id AS purchaseId, commodity, is_agree AS isAgree, supplier, quantity, amount_money AS amountMoney, payment_method AS paymentMethod, is_pay AS isPay, has_take_goods AS hasTakeGoods, operator, purchase_time AS purchaseTime,classify FROM purchase WHERE
classify=#{classify}
AND
operator=#{operator}

SELECT purchase_id AS purchaseId, commodity, is_agree AS isAgree, supplier, quantity, amount_money AS amountMoney, payment_method AS paymentMethod, is_pay AS isPay, has_take_goods AS hasTakeGoods, operator, purchase_time AS purchaseTime,classify FROM purchase WHERE
has_take_goods=#{hasTakeGoods}
AND
operator=#{operator}

SELECT purchase_id AS purchaseId, commodity, is_agree AS isAgree, supplier, quantity, amount_money AS amountMoney, payment_method AS paymentMethod, is_pay AS isPay, has_take_goods AS hasTakeGoods, operator, purchase_time AS purchaseTime,classify FROM purchase WHERE
payment_method=#{paymentMethod}
AND
operator=#{operator}

